import React, { useState } from 'react';
import { useRegisterUserMutation } from '../auth/authApiSlice';
import { Link } from 'react-router-dom';

function RegisterForm() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [email, setEmail] = useState('');
    const [roles] = useState(['Manager']); // Set 'Manager' as the default role

    const [registerUser, { isLoading }] = useRegisterUserMutation();

    const handleRegister = async (e) => {
        e.preventDefault();
        if (password !== confirmPassword) {
            // Handle mismatched passwords here, e.g., set an error state or show a message
            return;
        }
        try {
            await registerUser({ username, password, email, roles }).unwrap();
            // Handle success - e.g., show success message, redirect to login, etc.
        } catch (err) {
            // Handle error - e.g., show error message
        }
    };

    return (
        <form onSubmit={handleRegister}>
            <div>
                <label htmlFor="username">Username</label>
                <input 
                    type="text" 
                    id="username" 
                    value={username} 
                    onChange={(e) => setUsername(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="password">Password</label>
                <input 
                    type="password" 
                    id="password" 
                    value={password} 
                    onChange={(e) => setPassword(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="confirmPassword">Confirm Password</label>
                <input 
                    type="password" 
                    id="confirmPassword" 
                    value={confirmPassword} 
                    onChange={(e) => setConfirmPassword(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="email">Email</label>
                <input 
                    type="email" 
                    id="email" 
                    value={email} 
                    onChange={(e) => setEmail(e.target.value)}
                />
            </div>
            <button type="submit" disabled={isLoading}>
                {isLoading ? 'Registering...' : 'Register'}
            </button>
        </form>

    );
}

export default RegisterForm;
