// Inside PasswordResetRequest.js

import React, { useState } from 'react';
import { useSendPasswordResetEmailMutation } from './apiSlice'; // adjust the import path as needed
import { useHistory } from 'react-router-dom'; // use react-router-dom for navigation

const PasswordResetRequest = () => {
    const [email, setEmail] = useState('');
    const [sendPasswordResetEmail, { isSuccess }] = useSendPasswordResetEmailMutation();
    const history = useHistory();
    const dispatch = useDispatch();

    const handleSubmit = async (e) => {
        e.preventDefault();
        await sendPasswordResetEmail(email);
        if (isSuccess) {
            history.push('/reset-password');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                required
            />
            <button type="submit">Send Reset Link</button>
        </form>
    );
};

export default PasswordResetRequest;
