import { useState } from 'react';
import { usePasswordResetRequestMutation } from './AApiSlice';

const PasswordResetRequest = () => {
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
    const [passwordResetRequest, { isLoading }] = usePasswordResetRequestMutation();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await passwordResetRequest({ email }).unwrap();
            setMessage(response.message);
        } catch (err) {
            setMessage(err.data?.message || 'Failed to send password reset email');
        }
    };

    return (
        <section>
            <h1>Reset Password</h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor="email">Email:</label>
                <input
                    type="email"
                    id="email"
                    onChange={(e) => setEmail(e.target.value)}
                    value={email}
                    required
                />
                <button disabled={isLoading}>Send Reset Link</button>
            </form>
            {message && <p>{message}</p>}
        </section>
    );
};

export default PasswordResetRequest;
