import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { useSendPasswordResetEmailMutation } from './authApiSlice';
import { setResetPasswordEmail } from './authSlice'; // Adjust import path

const PasswordResetRequest = () => {
    const [email, setEmail] = useState('');
    const [sendPasswordResetEmail, { isSuccess }] = useSendPasswordResetEmailMutation();
    const history = useNavigate();
    const dispatch = useDispatch();

    const handleSubmit = async (e) => {
        e.preventDefault();
        await sendPasswordResetEmail(email).unwrap();
        if (isSuccess) {
            // Dispatch the email to Redux store
            dispatch(setResetPasswordEmail(email));
            history('/reset-password');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
            />
            <button type="submit">Send Reset Link</button>
        </form>
    );
};

export default PasswordResetRequest;
